import { Api } from '../../API/Api'
import { endPoints } from '../../API/Endpoint'
import { POST_LOGIN_CREDENTIAL } from '../ActionType'
import { useDispatch } from 'react-redux'

export const loginAction = (dispatch, reqpayload) => {
    console.log(reqpayload)
    Api.post(endPoints.LOGIN_CREDENTIAL, reqpayload).then((res) => {

        console.log("Searchdata===>", res.data.token)
        dispatch({
            type: POST_LOGIN_CREDENTIAL,
            payload: localstorage.setItem("logindata",res.data.token)

        })


    }).catch((err) => console.log(err.response))
}
