import React,{useEffect, useState} from 'react'
import ProductCard from './ProductCard'
import { Api } from '../API/Api'
import { endPoints } from '../API/Endpoint'



const SeasonalDecorslider = ({data}) => {
    const [SeasonalDecordata, setSeasonalDecordata] =useState()
  useEffect(()=>{
const requestpayload ={
    block_name: data.block_name,
    id: data.id

}
      Api.post(endPoints.GET_HOME_PAGE_DATA, requestpayload).then((res) => setSeasonalDecordata(res.data.data)).catch()

  })
    return (<>
        <div class="arrival-costumes">
            <div class="arrival-caption">
                <h2>Seasonal Decor</h2>
                <a class="shop-now" href="/product-listing-page/block?block_name=subcategory-product&amp;id=609244061e72134243c6d442">View All</a>
            </div>
            <div class="popular-costumes">
                {SeasonalDecorslider ? SeasonalDecorslider.map((item, id) => {
                    return
                    <ProductCard data={item} key={id} />

                }) : null

                }


            </div>
        </div>

    </>)
}

export default SeasonalDecorslider
