import React, { useEffect, useState } from 'react'
import mainmenuAction from '../Store/Action/MainmenuAction';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import SellerForm from './SellerForm';


const HeaderMenu = () => {
    const [sellerFormModalIsOpen, setSellerFormModalIsOpen] = useState(false)
    const navigate = useNavigate()
    let status = true

    const setSellerModalIsOpenTotrue = () => {
        if (status) {
            setSellerFormModalIsOpen(true)
        }
        // navigate(" / sell - product")
    }
    const closeModal = () => {
        setSellerFormModalIsOpen(false)
    }
    const dispatch = useDispatch()
    const MainMenuCategory = useSelector(state => state.MainMenuReducer.payload)
    // console.log(MainMenuCategory)
    useEffect(() => {
        dispatch(mainmenuAction)
    }, [dispatch])
    return (
        <>{sellerFormModalIsOpen && <SellerForm close={status} />}
            <div className='container'>
                <div className='main-menu'>
                    <ul className='menu'>
                        {MainMenuCategory ? MainMenuCategory.map((item, ind) => {
                            return (<Link to={`/product-filter-page/${item.slug}/${item._id}`} key={ind}><li className='sublist-menu ' >{item.name}</li></Link>)
                        }) : null}

                    </ul>
                </div>

                <Link to="/sell-product"><span className=" sell-text" onClick={setSellerModalIsOpenTotrue}>Register as a seller</span></Link>
            </div>
        </>
    )
}

export default HeaderMenu
