import React, { useEffect } from 'react'
import { useParams } from 'react-router-dom'
import { useDispatch, useSelector } from 'react-redux'
import getProductDetailData from '../Store/Action/GetProductDetailDataAction'
const ProductDetail = () => {
    const { id } = useParams()
    const dispatch = useDispatch()
    const data = useSelector(state => state.ProductPageReducer?.ProductPageData)
    console.log(data)
    useEffect(() => {
        getProductDetailData(dispatch, id)
    }, [dispatch])
    return (
        <>{data.map((item) => {
            return (<div><img src={item.default_image} alt="Shop Now" />

                <button class="complete-purchase">Add to cart</button></div>)
        })}



        </>
    )
}

export default ProductDetail
