import React, { useState } from 'react';
import { useSelector } from 'react-redux';
// import Autosuggest from 'react-autosuggest/dist/Autosuggest';
import Autosuggest from 'react-autosuggest';
{/*const allItems = [{
    name: 'ritu'
},
{
    name: 'sharma'
}]*/}

const AutosuggestComp = () => {
    const [value, setValue] = useState("");
    const [suggestions, setSuggestions] = useState([]);
    const [selected, setSelected] = useState("");
    const allItems = useSelector(state => state.SearchBarReducer.SearchBarData)

    const getSuggestionValue = suggestion => suggestion.name;

    const getSuggestions = value => {
        const inputValue = value.trim().toLowerCase();
        const inputLength = inputValue.length;

        return inputLength === 0
            ? []
            : allItems.filter(
                item => item.name.toLowerCase().slice(0, inputLength) === inputValue
            );
    };

    const renderSuggestion = suggestion => <div>{suggestion.name}</div>;

    const onChange = (event, { newValue }) => {
        setValue(newValue);
    };

    const onSuggestionsFetchRequested = ({ value }) => {
        setSuggestions(getSuggestions(value));
    };

    const onSuggestionsClearRequested = () => setSuggestions([]);

    const onSuggestionSelected = (event, { suggestion }) => {
        setSelected(suggestion.name);
    };

    const inputProps = {
        placeholder: "Type a character name",
        value,
        onChange
    };
    return (
        <Autosuggest
            suggestions={suggestions}
            onSuggestionsFetchRequested={onSuggestionsFetchRequested}
            onSuggestionsClearRequested={onSuggestionsClearRequested}
            onSuggestionSelected={onSuggestionSelected}
            getSuggestionValue={getSuggestionValue}
            renderSuggestion={renderSuggestion}
            inputProps={inputProps}
        />
    )
}

export default AutosuggestComp;
