        const requestpayload = {

            block_name: data.block_name,
            id: data.id
        }
        console.log(requestpayload)
        Api.post(endPoints.GET_HOME_PAGE_DATA, requestpayload).then(res => {
            console.log("tre", res.data.data)
            SetTrendSliderdata(res.data.data)

