import React, { useEffect } from 'react'
import { useParams } from 'react-router-dom'
import { useDispatch, useSelector } from 'react-redux'
import getProductDetailData from '../Store/Action/GetProductDetailDataAction'
const ProductDetail = () => {
    const { id } = useParams()
    const dispatch = useDispatch()
    const data = useSelector(state => state.ProductPageReducer?.ProductPageData)
    console.log(data)
    useEffect(() => {
        getProductDetailData(dispatch, id)
    }, [dispatch])
    return (
        <>
            {data.map((item, index) => {
                return (<>
                    <div key={index}> <img src={item.default_image} alt="Shop Now" /></div>
                    <div >
                        <h2>Item Detail</h2>
                        {item.brandData.map((brand)=>{
                            return(<h4>Brand:
                                {brandData.name ? brandData.name:""}
                                </h4>)
                        })}

                        {item.Condition ? <h4>Condition:{item.Condition}</h4> : ""}
                        <h4>ShippingCost : {item.shippingCost ? item.shippingCost : "Free"}

                        </h4>
                    </div>
                    {
                        item.Attrs.map((attrs, index) => {
                            return (
                                <div key={index}>
                                    {attrs.name ? <h4>{attrs.name} : {attrs.value}</h4> : ""}
                                </div>

                            )

                        })



                    }


                </>)
            })







            }



        </>
    )
}

export default ProductDetail
