import { GET_SEARCHBAR_DATA } from '../ActionType'
const initialstate = {
    SearchBarData: [],
    SearchBarResponse :[]
}

export const searchBarReducer = (state = initialstate, action) => {

    switch (action.type) {
        case GET_SEARCHBAR_DATA: return {
            ...state,
            SearchBarData: action.payload

        }




        default: return state;

    }
}
