import React, { useState } from 'react'
import logo from '../Assets/Image/logo.png'
import SearchBarAction from '../Store/Action/SearchBarAction'
import { useDispatch } from 'react-redux'
import { useSelector } from 'react-redux'
import Autosuggest from '../c'


import { Link } from 'react-router-dom'





const Header = () => {
    const [Input, SetInput] = useState('')
    const [Suggestion, SetSuggestion] = useState([])
    const [filteredSuggestions, setFilteredSuggestions] = useState([]);
    const [activeSuggestionIndex, setActiveSuggestionIndex] = useState(0);
    const Searchbardata = useSelector(state => state.SearchBarReducer.SearchBarData)

    // console.log(Searchbardata)

    const dispatch = useDispatch()
    const changeHandler = (e) => {

        SetInput(e.target.value.toLowerCase())
        SearchBarAction(dispatch, { searchValue: Input })
        if (Input.length !== 0) {
            SetSuggestion(Searchbardata)
        }
        else {
            SetSuggestion([])

        }

    }

    // console.log("sugg", Suggestion)

    const filterdata = Suggestion.filter(element => {
        return element.Title.toLowerCase().includes(Input.toLowerCase());
    });
    // console.log("filter", filterdata)


    // useEffect(() => {

    //     dispatch(SearchBarAction, Input)

    // }, [dispatch]);




    return (
        <>

            <div className="header-login">
                <div className="container">
                    <div className="login">
                        <div>
                            <span className="logo-class">
                                <img src={logo} alt="Bazaar" />
                            </span>
                        </div>
                        <div className="search-brand">
                            <form>
                                <div>
                                    <input type="text" autoComplete="off" aria-autocomplete="list" aria-controls="react-autowhatever-1" className="react-autosuggest__input" placeholder="Search for anything" value={Input} onChange={changeHandler} />
                                    <div className='Listitem'>
                                        {/*  { <ul>

                                            {(filterdata) ? filterdata.map((item, index) => {
                                                return (

                                                    <Link to={`/products/${item._id}`} key={index} >
                                                        {item.Title}
                                                    </Link>

                                                )
                                            }) : <div></div>}
                                        </ul>}*/}

                                    </div>
                                </div>
                            </form>
                            <i className="icon-search"></i>
                        </div>
                        <div className="header-address">
                            <div className="product-location-wraps">
                                <div className="product-location">
                                    <div className="location-mark">
                                        <i className="fas fa-map-marker-alt"></i>
                                        <div className="location-text">
                                            <div className="deliver-text">Deliver to</div>
                                            <div className="wrap-delivery">
                                                <div className="delivery-text">Select location</div>
                                                <div className="deliver-zip"></div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                        <div className="signup-register">
                            <div style={{ display: 'flex' }}>
                                <div className="signin-register">
                                    <span className="header-text">Sign Up</span>
                                </div>
                                <div className="signin-register" style={{ paddingRight: '20px' }}>
                                    <span className="header-text">Sign In</span>
                                </div>
                            </div>
                            <div className="cart-wrap">
                                <a className="wish-list msg-number" href="/my-cart" >
                                    <div className="tooltip">Cart</div>
                                    <i className="fas fa-shopping-bag"></i>
                                </a>
                            </div>
                        </div>
                    </div>
                </div>
            </div >


        </>
    )
}

export default Header
