import React, { useEffect } from 'react'
import { useParams } from 'react-router-dom'
import { useDispatch, useSelector } from 'react-redux'
import getProductDetailData from '../Store/Action/GetProductDetailDataAction'
const ProductDetail = () => {
    const { id } = useParams()
    const dispatch = useDispatch()
    const data = useSelector(state => state.ProductPageReducer?.ProductPageData)
    console.log(data)
    useEffect(() => {
        getProductDetailData(dispatch, id)
    }, [dispatch])
    return (
        <>{data.map((item) => {

            return (<div>
                <div> <img src={item.default_image} alt="Shop Now" /></div>
                <div>
                <h1>Item Detail</h1>
                <h4>Condition: {item.Condition ? item.Condition : ""}</h4>
                <h4>ShippingCost:{item.shippingCost ? item.shippingCost : ""}</h4>
                </div>
               {
                <div>



                </div>

                }

                <button className="complete-purchase">Add to cart</button>





            </div>)
        })}


        </>
    )
}

export default ProductDetail
