import React, { useEffect, useState, useRef } from 'react'
import logo from '../Assets/Image/logo.png'
import Login from './Login';
// import AutoLogout from './useAutoLogout'
import SignUp from './SignUp';
import { useDispatch, useSelector } from 'react-redux';
import AutosuggestComp from '../Component/AutoSuggest'
import { logoutAction } from '../Store/Action/LoginAction';
import HeaderMenu from './HeaderMenu'



const Header = () => {
    const [showModal, setShowModal] = useState(false)
    const logoutTimerIdRef = useRef(null);
    const [showLoginModal, setShowLoginModal] = useState(false)
    const dispatch = useDispatch()
    const setModalIsOpenTotrue = () => {
        setShowModal(true)
    }

    const setLoginModalIsOpenTotrue = () => {
        setShowLoginModal(true)
    }
    const MainMenuCategory = useSelector(state => state.MainMenuReducer.payload)

    useEffect(() => {
        const autoLogout = () => {
            if (document.visibilityState === 'hidden') {
                const timeOutId = window.setTimeout(logoutAction, 1 * 10 * 1000);
                logoutTimerIdRef.current = timeOutId;
            } else {
                window.clearTimeout(logoutTimerIdRef.current);
            }

        };

        document.addEventListener('visibilitychange', autoLogout);

        return () => {
            document.removeEventListener('visibilitychange', autoLogout);
        };

    }, []);
    const token = localStorage.getItem("logintoken")
    const getwishlist = () => {
        const reqpayload = 'Bearer ' + token
        console.log(reqpayload)

    }


    return (
        <>

            {showModal && <SignUp onClose={setShowModal} />}
            {showLoginModal && <Login onClose={setShowLoginModal} />}
            <div className="header-login">
                <div className="container">
                    <div className="login">
                        <div>
                            <span className="logo-class">
                                <img src={logo} alt="Bazaar" />
                            </span>
                        </div>
                        <div className="search-brand">
                            <form>
                                <div>
                                    < AutosuggestComp />
                                    {/*<input type="text" autoComplete="off" aria-autocomplete="list" aria-controls="react-autowhatever-1" className="react-autosuggest__input" placeholder="Search for anything" value={Input} onChange={changeHandler} />*/}
                                    <div className='Listitem'>
                                        {/*  { <ul>

                                            {(filterdata) ? filterdata.map((item, index) => {
                                                return (

                                                    <Link to={`/products/${item._id}`} key={index} >
                                                        {item.Title}
                                                    </Link>

                                                )
                                            }) : <div></div>}
                                        </ul>}*/}


                                    </div>
                                </div>
                            </form>
                            <i className="icon-search"></i>
                        </div>
                        <div className="header-address">
                            <div className="product-location-wraps">
                                <div className="product-location">
                                    <div className="location-mark">
                                        <i className="fas fa-map-marker-alt"></i>
                                        <div className="location-text">
                                            <div className="deliver-text">Deliver to</div>
                                            <div className="wrap-delivery">
                                                <div className="delivery-text">Select location</div>
                                                <div className="deliver-zip"></div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>


                        <div className="signup-register">



                            <div style={{ display: 'flex' }}>
                                <div className="signin-register">
                                    <span className="header-text" onClick={setModalIsOpenTotrue}>Sign Up</span>
                                </div>

                                {localStorage.getItem('logintoken') ?
                                    <div className="signin-register" style={{ paddingRight: '20px' }}>
                                        <span className="header-text" onClick={() => dispatch(logoutAction)}>Sign out</span>
                                    </div>
                                    :
                                    <div className="signin-register" style={{ paddingRight: '20px' }}>
                                        <span className="header-text" onClick={
                                            setLoginModalIsOpenTotrue}>Signin</span>
                                    </div>}


                            </div>

                            <div className="cart-wrap">
                                <a className="wish-list msg-number" href="/my-cart" >
                                    <div className="tooltip">Cart</div>
                                    <i className="fas fa-shopping-bag"></i>
                                </a>
                            </div>
                            {(token) ?
                                <div className="download-app">
                                    <a href="/profile/wishList">
                                        <span className="header-text">
                                            <span className="unread-wishlist">8 </span>
                                            <i className="fas fa-heart"></i></span>
                                    </a>
                                </div> : null}
                        </div>
                    </div>
                </div>
            </div >


            <HeaderMenu />

        </>
    )
}

export default Header
