import React from "react";
import { useState } from "react";
import ReactDOM from "react-dom";
import AutoSuggest from "react-autosuggest";
import SearchBarAction from '../Store/Action/SearchBarAction'
import { useDispatch } from 'react-redux'
import { useNavigate } from 'react-router-dom'
import { useSelector } from 'react-redux';

const lowerCasedCompanies = companies.map(company => {
    return {
        id: company.id,
        name: company.name.toLowerCase()
    };
});

export const Auto = () => {
    const [value, setValue] = useState("");
    const [suggestions, setSuggestions] = useState([]);
    const dispatch = useDispatch()

    function getSuggestions(value) {
        return lowerCasedCompanies.filter(company =>
            company.name.includes(value.trim().toLowerCase())
        );
    }
    const allItems = useSelector(state => state.SearchBarReducer.SearchBarData)
    const lowerCasedCompanies = allItems.map(item => {
        return {
            id: item._id,
            Title: item.Title.toLowerCase()
        };
    });
    return (
        <div>
            <AutoSuggest
                suggestions={suggestions}
                onSuggestionsClearRequested={() => setSuggestions([])}
                onSuggestionsFetchRequested={({ value }) => {
                    console.log(value);
                    setValue(value);
                    setSuggestions(getSuggestions(value));
                }}
                onSuggestionSelected={(_, { suggestionValue }) =>
                    console.log("Selected: " + suggestionValue)
                }
                getSuggestionValue={suggestion => suggestion.name}
                renderSuggestion={suggestion => <span>{suggestion.name}</span>}
                inputProps={{
                    placeholder: "Type 'c'",
                    value: value,
                    onChange: (_, { newValue, method }) => {
                        setValue(newValue);
                        SearchBarAction(dispatch, { searchValue: newValue })
                    }
                }}
                highlightFirstSuggestion={true}
            />
        </div>
    );
};
